<section class="bg-gray-900 text-white py-16 px-4 sm:px-6 lg:px-8">
  <div class="max-w-7xl mx-auto">
    <div class="text-center mb-12">
      <h1 class="text-4xl font-bold mb-4 animate-fade-in">{headline}</h1>
