        <a href="#" class="bg-blue-900 hover:bg-blue-800 text-white font-bold py-3 px-6 rounded-lg transition-all duration-300 transform hover:scale-105 hover:shadow-lg animate-pulse-once">
          {primaryCTA}
        </a>
        <a href="tel:XXX-XXX-XXXX" class="text-blue-400 hover:text-blue-300 font-medium flex items-center justify-center">
          <Calendar class="w-5 h-5 mr-2" /> {secondaryCTA}
        </a>
      </div>
      <p class="mt-6 text-gray-400">{trustSignal}</p>
    </div>

    <div class="max-w-md mx-auto bg-gray-800 p-8 rounded-lg shadow-xl">
      <form class="space-y-6">
        <div>
          <label for="name" class="block text-sm font-medium text-gray-300 mb-1">{formFields.name}</label>
