---
import { Star, User } from '@lucide/astro';

const testimonials = [
  {
    id: 1,
    name: "Sarah Johnson",
    role: "Home Buyer",
    rating: 5,
    quote: "The team at Real Estate Agency made our dream home a reality. Their expertise and dedication were unmatched!",
    avatar: "/images/avatars/Avatar_man.avif"
  },
  {
    id: 2,
    name: "Michael Chen",
    role: "Property Investor",
    rating: 5,
    quote: "We found our perfect investment property through their extensive network. Highly recommended!",
    avatar: "/images/avatars/Avatar_man.avif"
  },
  {
    id: 3,
    name: "Emily Rodriguez",
    role: "First-Time Homeowner",
    rating: 5,
    quote: "The guidance we received was invaluable. We couldn't have done it without their support.",
    avatar: "/images/avatars/Avatar_man.avif"
  },
  {
    id: 4,
    name: "David Wilson",
    role: "Commercial Property Owner",
    rating: 5,
    quote: "Their knowledge of the market helped us secure the best deal. Professional and trustworthy.",
    avatar: "/images/avatars/Avatar_man.avif"
  },
  {
    id: 5,
    name: "Jessica Martinez",
    role: "Relocating Family",
    rating: 5,
    quote: "They made our move seamless and stress-free. We're so grateful for their assistance.",
    avatar: "/images/avatars/Avatar_woman2.avif"
  }
]
const headline = "What Our Clients Say"
const subheadline = "Real Stories, Real Success";
---

<section class="py-12 bg-gray-50 bg-gray-100">
  <div class="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
    <div class="text-center mb-12">
      <h2 class="text-3xl font-bold text-gray-900 mb-4">{headline}</h2>
      <p class="text-xl text-gray-600">{subheadline}</p>
    </div>

    <div class="relative">
      <div class="overflow-hidden">
        <div class="flex space-x-6 pb-6 -mx-4 sm:-mx-6 lg:-mx-8 px-4 sm:px-6 lg:px-8">
          {testimonials.map((testimonial) => (
            <div  class="flex-shrink-0 w-full sm:w-1/2 lg:w-1/3 xl:w-1/4 p-4">
              <div class="bg-white rounded-lg shadow-md p-6 h-full flex flex-col">
                <div class="flex items-center mb-4">
                  <div class="w-12 h-12 rounded-full overflow-hidden mr-4">
                    <img src={testimonial.avatar} alt={testimonial.name} class="w-full h-full object-cover" />
                  </div>
                  <div>
                    <h3 class="font-semibold text-gray-900">{testimonial.name}</h3>
                    <p class="text-sm text-gray-600">{testimonial.role}</p>
                  </div>
                </div>
                <div class="flex mb-4">
                  {[...Array(testimonial.rating)].map((_, i) => (
                    <Star  class="w-5 h-5 text-yellow-400 fill-current" />
                  ))}
                </div>
                <p class="text-gray-700 mb-4 flex-grow">"{testimonial.quote}"</p>
                <div class="flex justify-end">
                  <User class="w-6 h-6 text-blue-600" />
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      <button class="absolute top-1/2 left-0 transform -translate-y-1/2 -ml-4 bg-white rounded-full w-10 h-10 flex items-center justify-center shadow-md hover:bg-gray-100 transition-colors">
        <svg class="w-6 h-6 text-gray-600" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
          <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M15 19l-7-7 7-7"></path>
        </svg>
      </button>

      <button class="absolute top-1/2 right-0 transform -translate-y-1/2 -mr-4 bg-white rounded-full w-10 h-10 flex items-center justify-center shadow-md hover:bg-gray-100 transition-colors">
        <svg class="w-6 h-6 text-gray-600" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
          <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M9 5l7 7-7 7"></path>
        </svg>
      </button>
    </div>
  </div>
</section>

<script>
  document.addEventListener('DOMContentLoaded', () => {
    const testimonialsContainer = document.querySelector('.overflow-hidden')
const testimonials = document.querySelectorAll('.flex-shrink-0')
const prevButton = document.querySelector('button:first-of-type')
const nextButton = document.querySelector('button:last-of-type');
    let currentIndex = 0;

    function updateSlider() {
      const offset = -currentIndex * (testimonials[0].offsetWidth + 24);
      testimonialsContainer.style.transform = `translateX(${offset}px)`;
    }

    prevButton.addEventListener('click', () => {
      if (currentIndex > 0) {
        currentIndex--;
        updateSlider();
      }
    });

    nextButton.addEventListener('click', () => {
      if (currentIndex < testimonials.length - 1) {
        currentIndex++;
        updateSlider();
      }
    });

    // Initialize slider
    updateSlider();
  });
</script>
